                    @endif

        {{-- Pagination Elements --}}
        @if ($paginator->lastPage() > 1)
            @php
                $current = $paginator->currentPage();
                $last = $paginator->lastPage();

                // Define window for max 4 pages
                $start = max($current - 2, 1);
                $end = min($current + 1, $last);

                if ($last > 4) {
                    if ($end == $last) $start = $end - 3;
                    if ($start == 1) $end = 4;
                }
            @endphp

            @for ($page = $start; $page <= $end; $page++)
                @if ($page == $current)
                    <span aria-current="page">
                        <span class="relative inline-flex items-center min-w-[35px] min-h-[35px] justify-center text-sm font-medium text-white bg-emerald-500 border border-emerald-500 rounded-lg leading-5">
                            {{ $page }}
                        </span>
                    </span>
                @else
                    <a href="{{ $paginator->url($page) }}"
                    class="relative inline-flex items-center min-w-[35px] min-h-[35px] justify-center text-sm font-medium text-emerald-500 bg-emerald-300/15 border border-emerald-500 cursor-pointer rounded-lg leading-5 hover:bg-emerald-500 hover:text-white transition"
                    aria-label="{{ __('Go to page :page', ['page' => $page]) }}">
                        {{ $page }}
                    </a>
                @endif
            @endfor
        @endif

